INSERT INTO `commenti` (`IdCommento`, `NomeUtente`, `Email`, `Testo`, `DataPubblicazione`, `Articolo`, `Voto`, `Risposta`) VALUES
(1, 'Michele "Axoras"', 'michele@axoras.com', 'Solo io ho l''impressione che &egrave; un pezzone?', '2016-04-18 11:41:39', 'notizia_21', 0, 0),
(2, 'Oudeis', 'ou@deis.com', 'La voce rovina tutto.', '2016-04-18 11:42:17', 'notizia_21', 0, 0),
(3, 'lux chaos', 'lux@chaos.it', 'Boh, per me son 4 minuti che durano 2 ore....grande sfoggio di tecnica come al solito, ma ci fosse 1 secondo che rimane in testa, 1 secondo sopra la media non solo dal punto di vista della tecnica....bah...', '2016-04-18 11:42:41', 'notizia_21', 0, 1),
(4, 'Vulgar Puppet', 'vulgar@puppet.it', 'A me loro non piacciono granch&egrave;, ma in quanto a sfoggio di tecnica c''&egrave; di molto peggio', '2016-04-18 11:43:08', 'notizia_21', -1, 0),
(5, 'Vitadathrasher', 'vita@da.it', 'Ci metterei anche dead skin mask, simbolo della capacit&agrave; di ricreare una sorta di colonna sonora.....thriller, cosa che non riesce a molte band ed &egrave; per questo che sono unici.', '2016-04-18 11:43:50', 'articolo_03', 0, 0),
(6, 'Macca', 'mac@gmail.it', 'Forse "Cult" da Christ Illusion come una delle loro canzoni musicalmente e tematicamente pi&ugrave; violente ci sarebbe stata...In ogni caso buona selezione, non &egrave; affatto facile sceglierne solo 15. Ottimo articolo come sempre.', '2016-04-18 11:45:35', 'articolo_03', 0, 0),
(7, 'gianmarco', 'gian@marco.it', 'Io avrei messo bitter peace da diabolus in musica . La traccia pi&ugrave; tradizionalista , gran mazzata nello stile slayer ', '2016-04-18 11:46:10', 'articolo_03', 0, 0),
(8, 'Radamanthis', 'rada@manth.com', 'L''ho ascoltato pi&ugrave; volte proprio qui su metallized e devo dire che &egrave; un disco spettacolare! E'' da un giorno che lo ascolto a nastro continuo! Degnissimo succerssore di quel capolavoro a nome Omnivium del 2011. Diedi 90 a quel disco, d&ograve; un 90 a questo. Band mostruosa, feroce e tecnica, fredda come il ghiaccio ma allo stesso tempo calda come il fuoco! Voto 90. ottimo!', '2016-04-18 11:46:58', 'recensione_01', 0, 0),
(9, 'Malleus', 'malleus@maleficarum.it', 'Ragazzi, per assimilarlo ci vogliono vari ascolti, ma &egrave; un disco che da veramente tanto, voto giustissimo. Ed io ero il primo a non avere pi&ugrave; grandi speranze dopo tutti i cambi di line-up..', '2016-04-18 11:47:49', 'recensione_01', 1, 0),
(10, 'Sicktadone', 'sicktadone@yahoo.it', 'Idem Malleus, sono stato piacevolmente sorpreso.', '2016-04-18 11:48:26', 'recensione_01', 0, 9),
(11, 'Macca', 'mac@ca.com', 'Io trovo che abbiano messo un p&ograve; troppa carne al fuoco, tanta confusione e tappeti di doppio pedale buttati l&igrave; un p&ograve; a caso. Concordo sul fatto che non siano tra i pi&ugrave; sbrodoloni del circondario, per&ograve; il pezzo sulle prime non mi entusiasma.', '2016-04-18 11:54:16', 'notizia_21', 1, 0);

--
-- Trigger `commenti`
--
DROP TRIGGER IF EXISTS `AumentaNumeroCommenti`;
DELIMITER //
CREATE TRIGGER `AumentaNumeroCommenti` AFTER INSERT ON `commenti`
 FOR EACH ROW BEGIN

	SET @check = SPLIT_STR( NEW.Articolo , '_' , 1) ;
    
    IF ( @check = 'articolo' ) THEN
    	 UPDATE Articoli
    	 SET NumeroCommenti = NumeroCommenti + 1
    	 WHERE IdSpeciale = NEW.Articolo ;
         
    ELSEIF ( @check = 'intervista' ) THEN
    	 UPDATE Interviste
    	 SET NumeroCommenti = NumeroCommenti + 1
    	 WHERE IdSpeciale = NEW.Articolo ;
         
    ELSEIF ( @check = 'live' ) THEN
		 UPDATE LiveReport
		 SET   NumeroCommenti = NumeroCommenti + 1
    	 WHERE IdSpeciale = NEW.Articolo ;
         
    ELSEIF ( @check = 'notizia' ) THEN
    	 UPDATE Notizie 
    	 SET  NumeroCommenti = NumeroCommenti + 1
    	 WHERE IdNotizia = NEW.Articolo ;
         
    ELSEIF ( @check = 'demo' ) THEN
    	 UPDATE Demo 
    	 SET   NumeroCommenti = NumeroCommenti + 1
    	 WHERE IdRecensione = NEW.Articolo ;
         
    ELSEIF ( @check = 'extra' ) THEN
    	 UPDATE ExtraMetal
    	 SET  NumeroCommenti = NumeroCommenti + 1
    	 WHERE IdRecensione = NEW.Articolo ;
         
    ELSEIF ( @check = 'rispolverati' ) THEN
    	 UPDATE Rispolverati
		 SET NumeroCommenti = NumeroCommenti + 1
		 WHERE IdRecensione = NEW.Articolo ;
         
    ELSEIF ( @check = 'recensione' ) THEN
    	 UPDATE UltimeUscite
    	 SET   NumeroCommenti = NumeroCommenti + 1
    	 WHERE IdRecensione = NEW.Articolo ;

   	END IF ;


END
//
DELIMITER ;
DROP TRIGGER IF EXISTS `DiminuisciNumeroCommenti`;
DELIMITER //
CREATE TRIGGER `DiminuisciNumeroCommenti` AFTER DELETE ON `commenti`
 FOR EACH ROW BEGIN

	SET @check = SPLIT_STR( OLD.Articolo , '_' , 1) ;
    
    IF ( @check = 'articolo' ) THEN
    	 UPDATE Articoli
    	 SET NumeroCommenti = NumeroCommenti - 1
    	 WHERE IdSpeciale = OLD.Articolo ;
         
    ELSEIF ( @check = 'intervista' ) THEN
    	 UPDATE Interviste
    	 SET NumeroCommenti = NumeroCommenti - 1
    	 WHERE IdSpeciale = OLD.Articolo ;
         
    ELSEIF ( @check = 'live' ) THEN
		 UPDATE LiveReport
		 SET   NumeroCommenti = NumeroCommenti - 1
    	 WHERE IdSpeciale = OLD.Articolo ;
         
    ELSEIF ( @check = 'notizia' ) THEN
    	 UPDATE Notizie 
    	 SET  NumeroCommenti = NumeroCommenti - 1
    	 WHERE IdNotizia = OLD.Articolo ;
         
    ELSEIF ( @check = 'demo' ) THEN
    	 UPDATE Demo 
    	 SET   NumeroCommenti = NumeroCommenti - 1
    	 WHERE IdRecensione = OLD.Articolo ;
         
    ELSEIF ( @check = 'extra' ) THEN
    	 UPDATE ExtraMetal
    	 SET  NumeroCommenti = NumeroCommenti - 1
    	 WHERE IdRecensione = OLD.Articolo ;
         
    ELSEIF ( @check = 'rispolverati' ) THEN
    	 UPDATE Rispolverati
		 SET NumeroCommenti = NumeroCommenti - 1
		 WHERE IdRecensione = OLD.Articolo ;
         
    ELSEIF ( @check = 'recensione' ) THEN
    	 UPDATE UltimeUscite
    	 SET   NumeroCommenti = NumeroCommenti - 1
    	 WHERE IdRecensione = OLD.Articolo ;

   	END IF ;


END
//
DELIMITER ;

