DROP TABLE IF EXISTS `inforedattore`;
CREATE TABLE IF NOT EXISTS `inforedattore` (
  `IdRedattore` int(11) NOT NULL,
  `Residenza` varchar(500) COLLATE utf8_bin NOT NULL,
  `DataNascita` varchar(500) COLLATE utf8_bin NOT NULL,
  `Email` varchar(500) COLLATE utf8_bin NOT NULL,
  `Immagine` varchar(500) COLLATE utf8_bin NOT NULL,
  `GruppiPreferiti` text COLLATE utf8_bin NOT NULL,
  `GeneriPreferiti` text COLLATE utf8_bin NOT NULL,
  `Interessi` text COLLATE utf8_bin NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

