INSERT INTO `inforedattore` (`IdRedattore`, `Residenza`, `DataNascita`, `Email`, `Immagine`, `GruppiPreferiti`, `GeneriPreferiti`, `Interessi`) VALUES
(1, 'Sardegna/Pisa', '17/10/1995', '', 'Here_Die.jpg', 'Necrophagist, Death, Atheist, Cynic, Suffocation, Cryptopsy, Dawn, Entombed, Dissection, Eucharist, Immortal,\r\nBurzum, Mayhem, Slayer, Metallica (primi), Anthrax, The Faceless, Gorod, Obscura, At The Gates, In Flames (primi),\r\nRage Against the Machine, Gorement, God Macabre, Ulver e tanti (troppi) altri validissimi da elencare.', 'Death Metal, Thrash Metal, Black Metal in tutte le loro combinazioni, oltre a tutto ciò che è valido e metallizzato.', 'Musica, Mirto, Informatica'),
(2, 'Firenze', '25/08/1976', '', 'Lizard.jpg', 'Black Sabbath, Led Zeppelin, Deep Purple, Iron Maiden, Mercyful Fate/King Diamond, Warrior Soul, Megadeth,\r\nJimi Hendrix, Monster Magnet, The Who, Kyuss, Jethro Tull, Soundgarden, Alice In Chains, Rush, EL&P, Yes,\r\nSlayer, Armored Saint, Savatage, Badlands, Pentagram, The Doors, Trouble, Solitude Aeturnus, Spiritual Beggars, UFO,\r\nThe Allman Brothers Band, Lynyrd Skynyrd, Cathedral, Down, Pearl Jam, Skyclad, Nevermore, Queensryche,\r\nA Perfect Circle, AC/DC, Motorhead, Riot, Pink Floyd, Black Crowes, Angel Witch, Marillion, Coroner, Pantera, Uriah Heep,\r\nRATM, Wicked Minds, DGM, Small Jackets e... tutti gli altri!!!', 'Hard Rock, NWOBHM, Doom, Stoner, Power/Thrash, Prog, Southern, Blues, Alternative,Psichedelia, Folk,\r\nCantautori italiani.', 'Cantare, scrivere, politica, fumetti.'),
(3, 'Messina', '19/03/1967', '', 'Raven.jpg', 'Iron Maiden, Metallica, Judas Priest, AC/DC, Tesla, Metal Church, Van Halen, Ronnie James Dio, Black Sabbath,\r\nOzzy Osbourne, Apocalyptica, Rhapsody, Slayer, Opeth, Dark Tranquillity, Amon Amarth, Suspyre ed infiniti altri.', 'Hard Rock , Heavy , Power/Epic e tutto quanto di valido e metallizzato esista ed è esistito dal 1965 ad oggi alle 8,30.\r\nSe qualcosa mi è sfuggita vuol dire che non è mai uscita ;-)', 'Musica , Arte, Antiquariato , Attualità ed ovviamente, dato che ho pubblicato un libro, letteratura.'),
(4, 'Torino', '28/07/1971', '', 'Forbiddenevil.jpg', 'Corrosion of Conformity, Death Angel, Forbidden, Nuclear Assault, Sacred Reich, Warbringer, Exodus, Black Sabbath,\r\nDark Angel, Venom, Motorhead, Onslaught, Evil Dead, Xentrix, Kreator, Coroner, Gojira, Celtic Frost, Grave,\r\nAlice in Chains, Rainbow, Soundgarden, Toxik, Destruction, Soziedad Alkoholika, Triptykon, Opeth, Rush, R.J. Dio, \r\nKiss, Carcass, Cattle Decapitation, Anthrax, Metallica (until 1989), Church of Misery, Graveyard, Death, Entombed,\r\nDefiance, Shrapnel, Warfuck, Asphyx, Mekong Delta, Metal Church, Angel Witch, Spiritual Beggars, AC/DC,\r\nIron Maiden, Sepultura, Exciter, Napalm Death, Testament, Overkill, Voivod, GBH, Primus, Possessed,...................and more.', 'Thrash Metal, Techno-Thrash Metal, Death Metal, Technical death Metal, Heavy Metal, Hard Rock, \r\nStoner, Prog, NWOBHM', 'Libri, Horror Movies, Viaggi, Animali.'),
(5, 'Angera (VA)', '26/07/1979', '', 'MyRefuge.jpg', 'Helloween, Rage, Annihilator, Gamma Ray, Cinderella, W.A.S.P., Savatage, Rhapsody, Mago de Oz, Primal Fear, Boston, \r\nDire Straits, Motley Crue, King Diamond, Iced Earth, Nightwish, Threshold, Scorpions, Running Wild,\r\nBlind Guardian, Avantasia, Edguy, Poison, Kamelot, Angra, Manowar, Blue Oyster Cult...', 'Metal classico e derivati, hard rock, glam, blues, progressive, death metal... tutta la musica fatta bene che emoziona', 'Suonare la chitarra, comporre, scrivere, moto, una buona birra con gli amici'),
(6, 'Fossano (CN)', '15/11/1990', '', 'Monky.jpg', 'Death, Cynic, Tool, The Faceless, Peste Noire, Devin Townsend, Pink Floyd, Dark Angel, Nevermore, Annihilator,\r\nKreator, Machine Head, Toxik, Realm, Testament, Necrophagist, Obscura, Savatage, Aspid, Gorod, Exivious, Rainbow,\r\nDeep Purple,Led Zeppelin, Symphony X, Textures, Pantera, Megadeth, Sodom, Destruction, Tankard, Agalloch, Emperor,\r\nSteven Wilson,The Aristocrats, Strapping Young Lad, Behemoth, Sadus, Fleshgod Apocalypse, Atheist e molti altri...', 'Thrash, Technical Death, Progressive, Ambient, Black, Death, Psychedelic, Power, Hard Rock, Jazz, Blues, Funky, etc...', 'Musica, Chitarra, Libri, Concerti, Cinema, Fumetti...'),
(7, 'Casale Monferrato (AL)', '', '', 'Matocc.jpg', 'Slayer, Iron Maiden, Queen, AC/DC, Metallica, Exodus, Megadeth, Lamb of God, Kreator, Meshuggah, Mastodon, Kiss, \r\nPantera, Deep Purple, Led Zeppelin, The Rolling Stones, Wolfmother, Jethro Tull, The Who, Machine Head, Black Sabbath,\r\nOzzy Osbourne, Fear Factory, Sepultura, Testament, Aerosmith, Elio e le Storie Tese, Timoria, Helloween, Van Halen,\r\nLucio Battisti, Fabrizio De Andre'', Litfiba, Dire Straits, Judas Priest, Pink Floyd, U2, Dream Theater, Air, Death, Cynic,\r\nTool, Bee Gees, Depeche Mode, Guns N'' Roses, Toto, Enya, Eagles e altri 666 che non ho citato...', 'Thrash, Rock, Heavy, Hard Rock, Groove, Cyber Metal, Industrial, di tutto un po''...', 'Musica (ovviamente), cinema, letteratura (soprattutto classici horror), fumetti (colleziono Dylan Dog), zoologia.'),
