--
-- Struttura della tabella `notizie`
--

DROP TABLE IF EXISTS `notizie`;
CREATE TABLE IF NOT EXISTS `notizie` (
  `IdNotizia` varchar(255) COLLATE utf8_bin NOT NULL DEFAULT 'notizia_',
  `NomeBand` varchar(2000) COLLATE utf8_bin NOT NULL,
  `Cartella` varchar(255) COLLATE utf8_bin NOT NULL,
  `Cover` varchar(13) COLLATE utf8_bin NOT NULL DEFAULT 'Anteprima.jpg',
  `Titolo` varchar(255) COLLATE utf8_bin NOT NULL,
  `Testo` text COLLATE utf8_bin NOT NULL,
  `DataPubblicazione` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `NumeroCommenti` int(11) NOT NULL DEFAULT '0',
  `IdRedattore` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

--
-- Dump dei dati per la tabella `notizie`
--

INSERT INTO `notizie` (`IdNotizia`, `NomeBand`, `Cartella`, `Cover`, `Titolo`, `Testo`, `DataPubblicazione`, `NumeroCommenti`, `IdRedattore`) VALUES
('notizia_01', 'Carcass', '01Carcass', 'Anteprima.jpg', 'Carcass: live a luglio in provincia di Cagliari', 'I deathster britannici Carcass torneranno in Italia per una data a luglio in occasione del Solk N Roll Festival che si svolgerà presso l''Arena Fenicia di Sant'' Antioco (Cagliari).\r\nEcco i primi dettagli:!_br_!!_br_!\r\n!_cite_!\r\nCarcass + !_br_!\r\nguest !_br_! !_br_!\r\n\r\nSabato 02 Luglio @ Solk N Roll Festival - Arena Fenicia, Sant'' Antioco (Cagliari)!_br_!\r\nBiglietti 20 euro !_br_!\r\nOrari TBA !_/cite_! !_br_! !_br_!\r\n!_a href="https://www.facebook.com/events/185861508447981/" _! Evento Facebook !_/a_! !_br_! !_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/AfjYYh2AWrs" allowfullscreen>!_/iframe_!', '2016-02-23 14:01:18', 0, 22),
('notizia_02', 'Augury', '02Augury', 'Anteprima.jpg', 'Augury: si separano dal batterista', 'La formazione prog death canadese Augury ha annunciato di essersi separata dallo storico batterista Etienne Gallo, che era rientrato nel gruppo gruppo.\r\nLa band ha comunque dichiarato di aver quasi finito le registrazioni delle parti di chitarra, mentre la settimana prossima cominceranno quelle di basso ed al termine di queste inizieranno la ricerca del nuovo batterista. !_br_! !_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/5DPW9ED1WBs" allowfullscreen_!!_/iframe_!', '2016-02-23 14:28:51', 0, 22),
('notizia_03', 'Fallujah', '03Fallujah', 'Anteprima.jpg', 'Fallujah: guarda il lyric video di ''The Void Alone''', 'Quello che vedete di seguito è il lyric video ufficiale di The Void Alone, brano degli statunitensi Fallujah che farà parte di Dreamless, il terzo disco della band che sarà pubblicato il 29 aprile dalla Nuclear Blast Records. !_br_! !_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/znjLSW8kfBM" allowfullscreen_!!_/iframe_!', '2016-02-23 14:34:19', 0, 22),
('notizia_04', 'Metallica', '04Metallica', 'Anteprima.jpg', 'Metallica: ad aprile le versioni rimasterizzate di ''Kill ''Em All'' e ''Ride The Lightning''', 'A quasi un anno e mezzo dall''annuncio, i Metallica hanno svelato i dettagli delle versioni rimasterizzate di Kill ''Em All e Ride The Lightning, che saranno pubblicato il 15 aprile in formato CD, vinile e deluxe boxset dall''etichetta della band. !_br_!\r\nEcco due estratti: !_br_! !_br_!\r\n!_iframe width="670" height="166" src="https://w.soundcloud.com/player/?url=https%3A//api.soundcloud.com/tracks/247435358&color=ff5500"_!!_/iframe_!\r\n!_iframe width="670" height="166" src="https://w.soundcloud.com/player/?url=https%3A//api.soundcloud.com/tracks/247435351&color=ff5500"_!!_/iframe_!', '2016-02-23 14:38:13', 0, 22),
('notizia_05', 'Novembre', '05Novembre', 'Anteprima.jpg', 'Novembre: ascolta il nuovo brano ''Umana''', 'I Novembre, che il prossimo 1 aprile faranno uscire per Peaceville Records l''album del loro ritorno, Ursa (maggiori dettagli in questa news), hanno appena pubblicato online il loro primo singolo.\r\n!_br_! !_br_!\r\nSi tratta di Umana, pezzo scritto originariamente otto anni fa ma che per l''occasione è stato rimaneggiato anche dal punto di vista delle lyrics, ampliandolo e creando un miscuglio di spunti derivanti dalla filosofia, dalla sociologia e dalla psicologia.\r\n!_br_! !_br_!\r\nEcco di seguito il lyric video:!_br_! !_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/bqpQ2Vkr7dk"  allowfullscreen_!!_/iframe_!', '2016-02-23 14:43:34', 0, 23),
('notizia_06', 'Ihsahn', '06Ihsahn', 'Anteprima.jpg', 'Ihsahn: posticipato di un mese ''Arktis''', 'A causa di non specificate motivazioni, la Candlelight Records ha annunciato di aver posticipato di circa un mese la pubblicazione di Arktis, il nuovo disco solista di Ihsahn inizialmente previsto per il 4 marzo e che invece uscirà l''8 aprile.\r\n!_br_! !_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/0VxbJb_Gs8w" allowfullscreen>!_/iframe_!\r\n', '2016-02-23 14:46:48', 0, 22),
('notizia_07', 'Vektor', '07Vektor', 'Anteprima.jpg', 'Vektor: ecco il trailer di ''Terminal Redux''', 'A pochi giorni dall''annuncio dei dettagli del nuovo disco, i thrasher statunitensi Vektor hanno diffuso il primo trailer realizzato per Terminal Redux, il terzo album della band in uscita il 6 maggio.\r\nEccolo di seguito: !_br_! !_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/MmYU449Xfxc" allowfullscreen_!!_/iframe_!\r\n', '2016-02-23 14:50:14', 0, 22),
('notizia_08', 'Incantation', '08Incantation', 'Anteprima.jpg', 'Incantation: tornano su Relapse Records, annunciato il nuovo album', 'Gli Incantation hanno annunciato di essere tornati nel roster della Relapse Records, con la quale erano sotto contratto dagli inizi degli anni ''90, quando venivano pubblicati lavori come Onward To Golgotha, Mortal Throne Of Nazarene, Diabolical Conquest e The Infernal Storm.\r\n!_p_!\r\nAl momento il gruppo guidato da John McEntee sta registrando i brani del prossimo disco, che verrà missato e masterizzato da Dan Swanö e che probabilmente verrà pubblicato questa estate.\r\n!_/p_!\r\nAncora non conosciamo maggiori dettagli in merito, quindi restiamo in attesa di aggiornamenti.!_br_!!_br_!\r\n!_iframe width="670" height="480" src="https://www.youtube.com/embed/Ogu8AbvKsKU" allowfullscreen_!!_/iframe_!', '2016-02-23 14:56:12', 0, 23),
('notizia_09', 'Entombed A.D.', '09Entombed', 'Anteprima.jpg', 'Entombed A.D.: primo video track by track del nuovo album ''Dead Dawn''', '"Dead Dawn" il nuovo disco degli ENTOMBED A.D. uscirà il 26 febbraio su Century Media Records. Di seguito il primo video track by track: !_br_! !_br_!\r\n!_iframe width="670" height="320" src="https://www.youtube.com/embed/7aqerPOO54k" allowfullscreen_!!_/iframe_!\r\n!_br_! !_br_!\r\nTracklist e copertina:\r\n!_pre_!\r\n!_cite_!\r\nMidas In Reverse\r\nDead Dawn\r\nDown To Mars To Ride\r\nAs The World Fell\r\nTotal Death\r\nThe Winner Has Lost\r\nSilent Assassin\r\nHubris Fall\r\nBlack Survival\r\nNot What It Seems\r\n!_/cite_!\r\n!_/pre_!\r\n!_img src="../Notizie/09Entombed/Notizia1.jpg" alt="Entombed" _!', '2016-02-23 15:00:12', 0, 24),
('notizia_10', 'Gojira', '10Gojira', 'Anteprima.jpg', 'Gojira: a febbraio un box set per il decimo anniversario del debutto', 'Il prossimo 12 febbraio uscirà per Listenable Records il box set in edizione limitata di From Mars To Sirius, album con cui i Gojira hanno debuttato per l''etichetta e che quest''anno compie dieci anni.\r\n!_br_! !_br_!\r\nAll''interno dell''edizione saranno compresi:!_br_!\r\n- Cassetta esclusiva di From Mars To Sirius, che sarà disponibile solo insieme al box !_br_!\r\n- Quattro disegni di Joe Duplantier mai stampati prima d''ora, che sono stati fatti prima che venisse pubblicata la cover art finale !_br_!\r\n- Bandiera con l''artwork di uno dei disegni !_br_!\r\n- Il plettro esclusivo di From Mars To Sirius !_br_!!_br_!\r\n\r\nPer i primi 500 acquirenti è riservata la possibilità di partecipare ad un contest per vincere una chitarra personalizzata con l''artwork del disco.\r\n!_br_! !_br_!\r\nVi ricordiamo che la band in questo momento è alle prese con i lavori per il nuovo album, che uscirà probabilmente per la primavera e di cui al momento non disponiamo di ulteriori dettagli.!_br_!!_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/duvEIDNNyhQ" allowfullscreen_!!_/iframe_!', '2016-02-23 17:57:40', 0, 22),
('notizia_11', 'Nero di Marte', '11Nero', 'Anteprima.jpg', 'Nero di Marte: dal vivo a Padova settimana prossima', 'I Nero di Marte, che fra poco si imbarcheranno in un tour Europeo con Gorguts, Psycroptic e Dyshrythmia il prossimo Marzo/Aprile, hanno in programma di suonare al Grindhouse di Padova il 27 febbraio; con loro i Sedna da Cesena e Awake the Sun di Venezia.!_br_!!_br_!\r\n!_a href="https://www.facebook.com/events/954165981327420/" _! Evento facebook !_/a_! !_br_!!_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/60rmOu_N5z8" allowfullscreen_!!_/iframe_!', '2016-02-23 15:08:49', 0, 25),
('notizia_12', 'Leprous', '12Leprous', 'Anteprima.jpg', 'Leprous: appello ai fan per realizzare un DVD live', 'I novergesi LEPROUS hanno annunciato che il prossimo 4 giugno, in occasione della loro esibizione al Rockfeller Music Hall di Oslo, registreranno il loro primo DVD live. Tuttavia, la band ha lanciato un appello ai fan perché li aiutino a coprire le spese necessarie alla realizzazione del release (affitto del locale, crew, editing, cura dei suoni, mixaggio, mastering, ecc).!_br_!\r\nDi seguito quanto hanno comunicato attraverso la loro pagina Facebook:\r\n!_br_! !_br_!\r\n!_cite_!\r\n"Grandi notizie! Il 4 giugno registreremo un live al Rockefeller Music Hall di Oslo! Ora chiediamo il vostro aiuto per trasformare questo progetto in realtà! Questo sarà un concerto molto speciale, con un sacco di effetti e di ospiti sul palco e dalle registrazioni risulteranno un live album e un DVD."!_/cite_!\r\n!_br_! !_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/I8_WDULwXYc" allowfullscreen_!!_/iframe_!', '2016-02-23 15:12:39', 0, 25),
('notizia_13', 'Nanowar of Steel', '13Agglutination', 'Anteprima.jpg', 'Agglutination Metal Festival 2016: nuova conferma', 'Dopo Therion, Exodus e Fleshgod Apocalypse, i Nanowar of Steel sono stati annunciati per l''Agglutination Metal Festival 2016. L''evento si terrà Domenica 21 agosto 2016 a Chiaromonte (PZ).\r\nAltre band verranno annunciate a breve.\r\n!_br_! !_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/miekjLzzCDk" allowfullscreen_!!_/iframe_!', '2016-02-23 15:22:59', 0, 25),
('notizia_14', 'Slayer', '14Slayer', 'Anteprima.jpg', 'Slayer: il video di ''You Against You''', 'Attraverso il player qui in basso sarà possibile vedere il video di You Against You, messo a disposizione dagli Slayer e diretto da BJ McDonnell.\r\nIl regista aveva già precedentemente collaborato con la band per la clip di Repentless. !_br_! !_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/2zhi3FmTUFg" allowfullscreen_!!_/iframe>', '2016-02-23 15:27:09', 0, 22),
('notizia_15', 'Death Angel', '15Angel', 'Anteprima.jpg', 'Death Angel: disponibile la tracklist ed un breve trailer del nuovo disco', 'I thrasher statunitensi Death Angel hanno svelato la tracklist che comporrà The Evil Divide, il nuovo disco della band prodotto da Jason Suecoff ed in uscita il 27 maggio sotto Nuclear Blast Records.\r\nEccola disponibile di seguito assieme ad un breve trailer: \r\n!_pre_!\r\n!_cite_!\r\nThe Moth\r\nCause For Alarm\r\nLost\r\nFather Of Lies\r\nHell To Pay\r\nIt Can''t Be This\r\nHatred United, United Hate\r\nBreakaway\r\nThe Electric Cell\r\nLet The Pieces Fall\r\n\r\nBonus:\r\nWasteland\r\n\r\n+ Making Of DVD\r\n!_/cite_!\r\n!_/pre_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/nvY5ZB3XXec" allowfullscreen_!!_/iframe>', '2016-02-23 15:35:30', 0, 25),
('notizia_16', 'Sodom', '16Sodom', 'Anteprima.jpg', 'Sodom: a 30 anni da ''Obsessed By Cruelty'', un''edizione speciale in vinile', 'Nel 1986, i SODOM pubblicarono due diverse versioni del loro album di debutto "Obsessed By Cruelty": una pubblicata da Metal Blade negli Stati Uniti e l''altra da Steamhammer in Germania. In occasione del trentesimo anniversario del release, entrambe le versioni saranno unite in un''unica collezione, completamente rimasterizzata, in uscita il prossimo 25 marzo. I vinili di ''Obsessed By Cruelty'' vengono resi disponibili per la prima volta dal 1986.\r\n!_p_!\r\nL''edizione, in esclusiva per \r\n!_a href= "https://www.facebook.com/waxmaniax/photos/a.195032020842393.1073741828.195024794176449/252769001735361/?type=3&theater" _! Wax Maniax !_/a_! sarà pre-ordinabile a partire dal 25 febbraio.!_/p_!\r\nPer maggiori informazioni, cliccare!_a href="http://www.waxmaniax.com/"_! qui !_/a_! .!_br_! !_br_!\r\n!_img src="../Notizie/16Sodom/Notizia1.jpg" alt="Sodom" _!\r\n!_img src="../Notizie/16Sodom/Notizia2.jpg" alt="Sodom" _!', '2016-02-23 15:38:45', 0, 23),
('notizia_17', 'Watchtower', '17Watchtower', 'Anteprima.jpg', 'Watchtower: accordo con Prosthetic Records e nuovo EP', 'La formazione prog thrash texana Watchtower ha raggiunto un accordo con la Prosthetic Records per la pubblicazione di Concepts Of Math: Book One EP, il nuovo EP della band che conterrà i tre brani recentemente diffusi in formato digitale assieme ad un brano inedito della durata di dieci minuti e The Size Of Matter del 2010.\r\nL''EP uscirà nel corso dell''anno in versione vinile e CD digipack limitata.\r\nEcco la tracklist:\r\n!_pre_!\r\n!_cite_!\r\nM-Theory Overture\r\nArguments Against Design\r\nTechnology Inaction\r\nThe Size Of Matter\r\nMathematica Calculis\r\n!_/cite_!\r\n!_/pre_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/AGGQmSMiMeE" allowfullscreen_!!_/iframe_!\r\n\r\n', '2016-02-23 15:44:24', 0, 24),
('notizia_18', 'Demilich', '18Demilich', 'Anteprima.jpg', 'Demilich: al Netherlands Deathfest con Danny Tunker', 'I Demilich si esibiranno al Netherlands Deathfest con Danny Tunker (Alkaloid, ex Aborted, Spawn of Possession) alla chitarra. Il leader Antti Boman si è infatti fratturato il polso cadendo dallo skateboard alcune settimane fa.\r\n!_br_! !_br_!\r\n!_iframe width="670" height="480" src="https://www.youtube.com/embed/XxizB02d0gE" allowfullscreen_!!_/iframe_!', '2016-02-23 15:48:01', 0, 24),
('notizia_19', 'Judas Priest', '19Judas', 'Anteprima.jpg', 'Judas Priest: i dettagli di ''Battle Cry'', nuovo CD/DVD live', 'Il prossimo 25 marzo la Epic Records pubblicherà Battle Cry, nuovo CD/DVD dal vivo dei Judas Priest registrato durante il tour di supporto all''ultimo album Redeemer Of Souls in occasione dell''apparizione della formazione heavy metal britannica al Wacken Open Air del 2015.\r\nLa parte DVD/Blu-ray comprenderà anche tre bonus track filmate lo scorso dicembre in Polonia.\r\nEcco la tracklist, mentre di lato è disponibile l''artwork:\r\n!_p_!\r\n!_pre_!\r\n!_cite_!\r\nBattle Cry CD sequence:\r\n\r\n(Intro) Battle Cry\r\nDragonaut\r\nMetal Gods\r\nDevil''s Child\r\nVictim of Changes\r\nHalls of Valhalla\r\nRedeemer of Souls\r\nBeyond the Realms of Death\r\nJawbreaker\r\nBreaking the Law\r\nHell Bent For Leather\r\nThe Hellion\r\nElectric Eye\r\nYou''ve Got Another Thing Coming\r\nPainkiller\r\n\r\n\r\nBattle Cry DVD/Blu-ray sequence:\r\n\r\n(Intro) Battle Cry\r\nDragonaut\r\nMetal Gods\r\nDevil''s Child\r\nVictim of Changes\r\nHalls of Valhalla\r\nRedeemer of Souls\r\nBeyond the Realms of Death\r\nJawbreaker\r\nBreaking the Law\r\nHell Bent For Leather\r\nThe Hellion\r\nElectric Eye\r\nYou''ve Got Another Thing Coming\r\nPainkiller\r\nLiving After Midnight\r\n\r\nRecorded live on August 1st, 2015 at Wacken Festival, Germany.\r\n\r\nBonus tracks:\r\nScreaming for Vengeance\r\nThe Rage\r\nDesert Plains\r\n\r\nRecorded live on December 10th, 2015 at The Ergo Arena, Gdansk, Poland.\r\n!_/cite_!\r\n!_/pre_!\r\n!_/p_!\r\n!_p_!\r\nDi seguito è disponibile una breve anteprima:\r\n!_/p_!\r\n!_iframe width="640" height="360" src="https://www.youtube.com/embed/hZH9ptg58ck" allowfullscreen_!!_/iframe>', '2016-02-23 15:51:18', 0, 23),
('notizia_20', 'Marduk', '20Marduk', 'Anteprima.jpg', 'Marduk: in Italia con Immolation e Origin', 'È stato divulgato in giornata il prossimo tour europeo dei Marduk, insieme a Immolation e Origin che passerà anche in Italia per una sola data, al Circolo Colony di Brescia il 17 maggio 2016.\r\n!_p_!\r\nMaggiori dettagli saranno pubblicati prossimamente.!_/p_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/0yPOqlrCl1I" allowfullscreen_! !_/iframe_!\r\n', '2016-02-23 16:00:09', 0, 24),
('notizia_21', 'Fallujah', '21Fallujah', 'Anteprima.jpg', 'Fallujah: ascolta il brano ''Scar Queen''', 'Grazie al player di soundcloud presente qui sotto è possibile ascoltare il brano Scar Queen della formazione prog death statunitense Fallujah estratto da Dreamless,il terzo disco della band che sarà pubblicato il 29 aprile dalla Nuclear Blast Entertainment.!_br_!!_br_!\r\n!_iframe width = "670" height="166" src="https://w.soundcloud.com/player/?url=https%3A//api.soundcloud.com/tracks/252807393&color=ff5500"_! !_/iframe_!\r\n!_br_!!_br_!\r\nLa band ha inoltre diffuso il video playthrough del brano, che potete vedere di seguito:!_br_!!_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/-ekrISzHG5A" allowfullscreen _! !_/iframe_!', '2016-03-21 09:42:29', 5, 22),
('notizia_22', 'Gorguts', '22Gorguts', 'Anteprima.jpg', 'Gorguts: ascolta il primo estratto dal nuovo EP', 'Ecco "Wandering Times", un estratto da "Pleiades'' Dust", il nuovo EP dei Gorguts!!_br_! !_br_!\r\n!_iframe width="670" height="360" src="https://www.youtube.com/embed/RDyn5lkVNlo" allowfullscreen_!!_/iframe_!\r\n!_br_!!_br_!\r\nIl leader Luc Lemay ha commentato:!_cite_! "Siccome il nostro nuovo EP Pleiades'' Dust consta di una singola composizione narrativa, non è stato scritto con l''intenzione di essere diviso in segmenti. Siccome però la storia si sviluppa per capitoli, sono stato in grado di isolare questa parte chiamata ''Wandering Times'', la quale dovrebbe dare all''ascoltatore una buona idea dell''estetica del pezzo. Il testo narra la storia della ''Casa della Sapienza'', ovvero una biblioteca di Baghdad molto popolare tra l''ottavo e il tredicesimo secolo. La maggior parte dell''attività intellettuale del Medioevo ebbe luogo là dentro, mentre l''Europa era avvolta nei Secoli Bui dopo la caduta dell''Impero Romano. Molte scoperte scientifiche come l''algebra, l''ottica e l''astronomia risalgono a quel periodo. Senza un''opera di traduzione non avremmo mai avuto il Rinascimento che conosciamo. Si tratta di una storia sulla curiosità, su menti favolose e, purtroppo, anche su come l''uomo distrugga enormi scoperte e traguardi".!_/cite_!!_br_!!_br_!\r\nIl lavoro, della durata di 33 minuti, verrà pubblicato il 13 maggio 2016 in digipak CD e LP.Artwork a cura di Zbigniew M. Bielak (Mayhem, Ghost,Zhrine).', '2015-11-24 11:49:12', 0, 24);

